mapper.select:
select userName,userID,role
from `user`
where userID = #{userID} and pwd = #{pwd};

mapper.update:
update user
set pwd = #{newpwd}
where userID = #{userID};

update user
set role = #{role}
where userID = #{userID}

update user
set userName = #{userName},pNum = #{pNum},userSex = #{userSex},userBirth = #{userBirth}
where userID = #{userID};

update user
set role = #{newrole}
where userID = #{userID};

mapper.select:
select count(*)
from  info
where userID = #{userID} and status = #{status};

mapper.update:
update info
set status = #{status}
where infoID = #{infoID};
